@l313l.ar_cmd(
    pattern="ÿ≠ÿ∏ÿ±_ŸÖÿ§ŸÇÿ™(?:\s|$)([\s\S]*)",
    command=("ÿ≠ÿ∏ÿ±_ŸÖÿ§ŸÇÿ™", plugin_category),
    info={
        "header": "To remove a user from the group for specified time.",
        "description": "Temporary bans the user for given time.",
        "Time units": {
            "s": "seconds",
            "m": "minutes",
            "h": "Hours",
            "d": "days",
            "w": "weeks",
        },
        "usage": [
            "{tr}tban <userid/username/reply> <time>",
            "{tr}tban <userid/username/reply> <time> <reason>",
        ],
        "examples": ["{tr}tban 2d to test baning for 2 days"],
    },
    groups_only=True,
    require_admin=True,
)
async def tban(event):  # sourcery no-metrics
    "ŸÑÿ≠ŸÄÿ∏ÿ± ÿ¥ÿÆÿµ ŸÖÿπ ŸàŸÇŸÄÿ™ ŸÖÿπŸäŸÄŸÜ"
    catevent = await edit_or_reply(event, "·ØΩÔ∏ô Ÿäÿ™ŸÄŸÖ  ÿßŸÑŸÄÿ≠ÿ∏ÿ± ŸÖÿ§ŸÇŸÄÿ™ÿß ÿ£ŸÜÿ™ŸÄÿ∏ÿ± **")
    user, reason = await get_user_from_event(event, catevent)
    if not user:
        return
    if not reason:
        return await catevent.edit("·ØΩÔ∏ô Ÿäÿ®ÿØŸà ÿßŸÜŸÉ ŸÑŸÖ ÿ™ŸÇŸÖ ÿ®Ÿàÿ∂ÿπ ŸàŸÇÿ™ ŸÖÿπ ÿßŸÑÿßŸÖÿ± **")
    reason = reason.split(" ", 1)
    hmm = len(reason)
    cattime = reason[0].strip()
    reason = "".join(reason[1:]) if hmm > 1 else None
    ctime = await extract_time(catevent, cattime)
    if not ctime:
        return
    if user.id == event.client.uid:
        return await catevent.edit(f"·ØΩÔ∏ô ÿπÿ∞ÿ±ÿß ŸÑÿß ŸäŸÖŸÉŸÜŸÜŸä ŸÉÿ™ŸÖ ŸÜŸÅÿ≥ŸÄŸä")
    await catevent.edit("·ØΩÔ∏ô ÿ™ŸÄŸÖ ÿ≠ŸÄÿ∏ÿ±Ÿá ŸÖŸÄÿ§ŸÇŸÄÿ™ÿß")
    try:
        await event.client(
            EditBannedRequest(
                event.chat_id,
                user.id,
                ChatBannedRights(until_date=ctime, view_messages=True),
            )
        )
    except UserAdminInvalidError:
        return await catevent.edit(
            "·ØΩÔ∏ô ** Ÿäÿ®ŸÄÿØŸà ÿ£ŸÜŸÉ ŸÑÿ≥ŸÄÿ™ ŸÖÿ¥ÿ±ŸÅ ŸÅŸä ÿßŸÑŸÖÿ¨ŸÖŸàÿπÿ© ÿßŸà ÿ™ÿ≠ÿßŸàŸÑ ŸÉÿ™ŸÖ ŸÖÿ¥ŸÄÿ±ŸÅ ŸáŸÜÿß**"
        )
    except BadRequestError:
        return await catevent.edit(NO_PERM)
    # Helps ban group join spammers more easily
    try:
        reply = await event.get_reply_message()
        if reply:
            await reply.delete()
    except BadRequestError:
        return await catevent.edit(
            "·ØΩÔ∏ô ** ŸÑŸÄŸäÿ≥ ŸÑÿØŸä ÿµŸÑÿßÿ≠ŸäŸÄÿßÿ™ ÿßŸÑÿ≠ÿ∞ŸÅ ŸÑŸÉŸÜ ÿ≥Ÿäÿ®ŸÇŸâ ŸÖÿ≠ÿ∏Ÿàÿ± ‚ùï**"
        )
    # Delete message and then tell that the command
    # is done gracefully
    # Shout out the ID, so that fedadmins can fban later
    if reason:
        await catevent.edit(
            f"**ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ {_format.mentionuser(user.first_name ,user.id)}** /n **ÿ™ŸÄŸÖ ÿ≠ÿ∏ÿ±Ÿá ÿ®ŸÜŸÄÿ¨ÿßÿ≠ ‚úÖ**\n"
            f"ŸÖŸÄÿØÿ© ÿßŸÑÿ≠ŸÄÿ∏ÿ± {cattime}\n"
            f"ÿßŸÑÿ≥ŸÄÿ®ÿ®:`{reason}`"
        )
        if BOTLOG:
            await event.client.send_message(
                BOTLOG_CHATID,
                "#ÿßŸÑŸÄÿ≠ÿ∏ÿ± ÿßŸÑŸÖŸÄÿ§ŸÇÿ™\n"
                f"**ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ : **[{user.first_name}](tg://user?id={user.id})\n"
                f"**ÿßŸÑÿØÿ±ÿØÿ¥ŸÄŸá : **{event.chat.title}(`{event.chat_id}`)\n"
                f"**ŸÖŸÄÿØÿ© ÿßŸÑÿ≠ŸÄÿ∏ÿ± : **`{cattime}`\n"
                f"**ÿßŸÑÿ≥ŸÄÿ®ÿ® : **__{reason}__",
            )
    else:
        await catevent.edit(
            f"** ÿßŸÑŸÄŸÖÿ≥ÿ™ÿÆÿØŸÖ {_format.mentionuser(user.first_name ,user.id)} \n **ÿ™ŸÄŸÖ ÿ≠ÿ∏ÿ±Ÿá ÿ®ŸÜŸÄÿ¨ÿßÿ≠ ‚úÖ** \n"
            f"**ŸÖŸÄÿØÿ© ÿßŸÑÿ≠ŸÄÿ∏ÿ±** {cattime}\n"
        )
        if BOTLOG:
            await event.client.send_message(
                BOTLOG_CHATID,
                "#ÿßŸÑŸÄÿ≠ÿ∏ÿ± ÿßŸÑŸÖŸÄÿ§ŸÇÿ™\n"
                f"**ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ : **[{user.first_name}](tg://user?id={user.id})\n"
                f"**ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ : **{event.chat.title}(`{event.chat_id}`)\n"
                f"**ŸÖŸÄÿØÿ© ÿßŸÑÿ≠ŸÄÿ∏ÿ± : **`{cattime}`",
            )

